import React, { useState } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:4000';

export default function VariableForm() {
  const [name, setName] = useState('');
  const [vid, setVid] = useState('');
  const [library, setLibrary] = useState('metrics');

  const addVariable = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post(`${API_URL}/variables`, { name, vid, library }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setName(''); setVid('');
      alert('Variable added!');
    } catch (err) {
      console.error(err.response?.data?.msg || err.message);
    }
  };

  return (
    <form onSubmit={addVariable}>
      <h4>Add Variable</h4>
      <input 
        placeholder="Name"
        value={name}
        onChange={e => setName(e.target.value)}
      /><br />
      <input 
        placeholder="Variable ID"
        value={vid}
        onChange={e => setVid(e.target.value)}
      /><br />
      <select value={library} onChange={e => setLibrary(e.target.value)}>
        <option value="metrics">Metrics</option>
        <option value="attributes">Attributes</option>
        <option value="values">Attribute Values</option>
      </select><br />
      <button type="submit">Add</button>
    </form>
  );
}
