import React from 'react';
import Editor from "@monaco-editor/react";

function CodeEditor({ code, setCode, variables, selectedPID }) {
  const handleCopy = () => {
    navigator.clipboard.writeText(code);
  };

  const handleClear = () => {
    setCode('// Start typing...');
  };

  return (
    <div>
      <Editor
        height="400px"
        defaultLanguage="javascript"
        value={code}
        onChange={setCode}
        theme="vs-dark"
        options={{
          minimap: { enabled: false },
          fontSize: 14,
          lineNumbers: 'on',
          automaticLayout: true,
          scrollBeyondLastLine: false,
        }}
      />
      <div style={{ 
        marginTop: '10px',
        display: 'flex',
        gap: '10px'
      }}>
        <button
          onClick={handleCopy}
          style={{
            width: '200px',
            padding: '8px',
            backgroundColor: '#4444ff',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Copy Code
        </button>
        <button
          onClick={handleClear}
          style={{
            width: '200px',
            padding: '8px',
            backgroundColor: '#f44336',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Clear Code
        </button>
      </div>
    </div>
  );
}

export default CodeEditor;
