import React from 'react';

function CodeEditor({ code, setCode, variables, selectedPID }) {
  const handleCopy = () => {
    navigator.clipboard.writeText(code);
  };

  const handleClear = () => {
    setCode('// Start typing...');
  };

  return (
    <div>
      <textarea
        value={code}
        onChange={(e) => setCode(e.target.value)}
        style={{
          width: '100%',
          height: '400px',
          padding: '12px',
          fontSize: '14px',
          fontFamily: 'monospace',
          border: '1px solid #ddd',
          borderRadius: '4px',
          backgroundColor: '#f8f9fa',
          resize: 'none',
          lineHeight: '1.5'
        }}
      />
      <div style={{ 
        marginTop: '10px',
        display: 'flex',
        gap: '10px'
      }}>
        <button
          onClick={handleCopy}
          style={{
            width: '200px',
            padding: '8px',
            backgroundColor: '#4444ff',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Copy Code
        </button>
        <button
          onClick={handleClear}
          style={{
            width: '200px',
            padding: '8px',
            backgroundColor: '#f44336',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Clear Code
        </button>
      </div>
    </div>
  );
}

export default CodeEditor;
