import React from 'react';
import AceEditor from 'react-ace';

// Import only what's needed
import 'ace-builds/src-min-noconflict/mode-javascript';
import 'ace-builds/src-min-noconflict/theme-monokai';
import 'ace-builds/src-min-noconflict/ext-language_tools';

function CodeEditor({ code, setCode, variables, selectedPID }) {
  const handleCopy = () => {
    navigator.clipboard.writeText(code);
  };

  const handleClear = () => {
    setCode('// Start typing...');
  };

  return (
    <div>
      <AceEditor
        mode="javascript"
        theme="monokai"
        value={code}
        onChange={setCode}
        name="code-editor"
        editorProps={{ $blockScrolling: true }}
        width="100%"
        height="400px"
        fontSize={14}
        showPrintMargin={true}
        showGutter={true}
        highlightActiveLine={true}
        setOptions={{
          enableBasicAutocompletion: true,
          enableLiveAutocompletion: true,
          enableSnippets: true,
          showLineNumbers: true,
          tabSize: 2,
        }}
      />
      <div style={{ 
        marginTop: '10px',
        display: 'flex',
        gap: '10px'
      }}>
        <button
          onClick={handleCopy}
          style={{
            width: '200px',
            padding: '8px',
            backgroundColor: '#4444ff',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Copy Code
        </button>
        <button
          onClick={handleClear}
          style={{
            width: '200px',
            padding: '8px',
            backgroundColor: '#f44336',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Clear Code
        </button>
      </div>
    </div>
  );
}

export default CodeEditor;
