import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CodeEditor from '../components/CodeEditor';
import VariableForm from '../components/VariableForm';

export default function MainPage() {
  const [code, setCode] = useState('// Start typing...');
  const [variables, setVariables] = useState([]);

  // Fetch variables when component mounts
  useEffect(() => {
    const fetchVariables = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:4000/variables', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setVariables(response.data);
      } catch (error) {
        console.error('Error fetching variables:', error);
      }
    };

    fetchVariables();
  }, []);

  // Function to handle adding new variable
  const handleVariableAdded = (newVariable) => {
    setVariables([...variables, newVariable]);
  };

  return (
    <div style={{ display: 'flex' }}>
      <div style={{ flex: 1 }}>
        <CodeEditor code={code} setCode={setCode} />
      </div>
      <div style={{ width: 300, marginLeft: 20 }}>
        <h2>Variables</h2>
        <div style={{ marginBottom: 20, maxHeight: '300px', overflowY: 'auto' }}>
          {variables.map((variable) => (
            <div 
              key={variable._id} 
              style={{
                padding: '10px',
                marginBottom: '5px',
                backgroundColor: '#f5f5f5',
                borderRadius: '4px',
                border: '1px solid #ddd'
              }}
            >
              <div><strong>Name:</strong> {variable.name}</div>
              <div><strong>Type:</strong> {variable.type}</div>
              <div><strong>Value:</strong> {variable.value}</div>
            </div>
          ))}
        </div>
        <VariableForm onVariableAdded={handleVariableAdded} />
      </div>
    </div>
  );
}
