import React from 'react';
import Editor from '@monaco-editor/react';

export default function CodeEditor({ code, setCode, variables }) {
  const handleEditorDidMount = (editor, monaco) => {
    // Register custom completion provider
    monaco.languages.registerCompletionItemProvider('javascript', {
      provideCompletionItems: (model, position) => {
        // Convert variables to completion items
        const suggestions = variables.map(variable => ({
          label: variable.name,
          kind: monaco.languages.CompletionItemKind.Variable,
          insertText: variable.name,
          detail: `${variable.type}: ${variable.value}`,
          documentation: `Variable of type ${variable.type} with value ${variable.value}`
        }));

        return {
          suggestions: suggestions
        };
      }
    });
  };

  return (
    <Editor
      height="80vh"
      defaultLanguage="javascript"
      value={code}
      onChange={setCode}
      onMount={handleEditorDidMount}
      options={{
        minimap: { enabled: false },
        suggestOnTriggerCharacters: true,
        quickSuggestions: true,
        wordBasedSuggestions: false // Disable default word-based suggestions
      }}
    />
  );
}
