import React, { useState } from 'react';

export default function PIDManager({ pids, onPIDSelect, onPIDAdd, selectedPID }) {
  const [showAddForm, setShowAddForm] = useState(false);
  const [newPIDName, setNewPIDName] = useState('');
  const [newPIDValue, setNewPIDValue] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onPIDAdd({
      name: newPIDName,
      pid: newPIDValue
    });
    setNewPIDName('');
    setNewPIDValue('');
    setShowAddForm(false);
  };

  return (
    <div style={{ marginBottom: '20px' }}>
      <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
        <select 
          value={selectedPID ? selectedPID.pid : ''}
          onChange={(e) => {
            const selected = pids.find(p => p.pid === e.target.value);
            onPIDSelect(selected);
          }}
          style={{
            padding: '8px',
            borderRadius: '4px',
            border: '1px solid #ddd',
            flex: 1
          }}
        >
          <option key="default" value="">
            {selectedPID ? `${selectedPID.name} (${selectedPID.pid})` : "Select Project"}
          </option>
          {pids && pids.map((pid) => (
            pid.pid !== selectedPID?.pid && (
              <option key={pid._id || pid.pid} value={pid.pid}>
                {`${pid.name || 'Unnamed'} (${pid.pid})`}
              </option>
            )
          ))}
        </select>
        <button
          onClick={() => setShowAddForm(true)}
          style={{
            padding: '8px 16px',
            backgroundColor: '#4CAF50',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Add PID
        </button>
      </div>

      {showAddForm && (
        <div style={{
          marginTop: '10px',
          padding: '15px',
          border: '1px solid #ddd',
          borderRadius: '4px'
        }}>
          <form onSubmit={handleSubmit}>
            <div style={{ marginBottom: '10px' }}>
              <label style={{ display: 'block', marginBottom: '5px' }}>
                Project Name:
              </label>
              <input
                type="text"
                value={newPIDName}
                onChange={(e) => setNewPIDName(e.target.value)}
                required
                style={{
                  width: '100%',
                  padding: '8px',
                  borderRadius: '4px',
                  border: '1px solid #ddd'
                }}
              />
            </div>
            <div style={{ marginBottom: '10px' }}>
              <label style={{ display: 'block', marginBottom: '5px' }}>
                PID:
              </label>
              <input
                type="text"
                value={newPIDValue}
                onChange={(e) => setNewPIDValue(e.target.value)}
                required
                style={{
                  width: '100%',
                  padding: '8px',
                  borderRadius: '4px',
                  border: '1px solid #ddd'
                }}
              />
            </div>
            <div style={{ display: 'flex', gap: '10px' }}>
              <button
                type="submit"
                style={{
                  padding: '8px 16px',
                  backgroundColor: '#4CAF50',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer'
                }}
              >
                Save
              </button>
              <button
                type="button"
                onClick={() => setShowAddForm(false)}
                style={{
                  padding: '8px 16px',
                  backgroundColor: '#f44336',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer'
                }}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
}
