import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CodeEditor from '../components/CodeEditor';
import VariableForm from '../components/VariableForm';

export default function MainPage() {
  const [code, setCode] = useState('// Start typing...');
  const [variables, setVariables] = useState([]);

  useEffect(() => {
    fetchVariables();
  }, []);

  const fetchVariables = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:4000/variables', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setVariables(response.data);
    } catch (error) {
      console.error('Error fetching variables:', error);
    }
  };

  const handleVariableAdded = (newVariable) => {
    setVariables([...variables, newVariable]);
  };

  const handleDeleteVariable = async (variableId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:4000/variables/${variableId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      // Remove the deleted variable from state
      setVariables(variables.filter(v => v._id !== variableId));
    } catch (error) {
      console.error('Error deleting variable:', error);
    }
  };

  return (
    <div style={{ display: 'flex' }}>
      <div style={{ flex: 1 }}>
        <CodeEditor code={code} setCode={setCode} />
      </div>
      <div style={{ width: 300, marginLeft: 20 }}>
        <h2>Variables</h2>
        <VariableForm onVariableAdded={handleVariableAdded} />
        <div style={{ 
          marginTop: 20, 
          maxHeight: '400px', 
          overflowY: 'auto',
          border: '1px solid #ddd',
          borderRadius: '4px',
          padding: '10px'
        }}>
          {variables.map((variable) => (
            <div 
              key={variable._id} 
              style={{
                padding: '10px',
                marginBottom: '5px',
                backgroundColor: '#f5f5f5',
                borderRadius: '4px',
                border: '1px solid #ddd',
                position: 'relative'
              }}
            >
              <div><strong>Name:</strong> {variable.name}</div>
              <div><strong>Value:</strong> {variable.value}</div>
              <button
                onClick={() => handleDeleteVariable(variable._id)}
                style={{
                  position: 'absolute',
                  top: '5px',
                  right: '5px',
                  background: '#ff4444',
                  color: 'white',
                  border: 'none',
                  borderRadius: '3px',
                  padding: '3px 8px',
                  cursor: 'pointer'
                }}
              >
                Ã—
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
