import React, { useState, useEffect, useRef } from 'react';

export default function CodeEditor({ code, setCode, variables, selectedPID }) {
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [cursorPosition, setCursorPosition] = useState(0);
  const [currentWord, setCurrentWord] = useState('');
  const [selectedIndex, setSelectedIndex] = useState(0);
  const [filteredSuggestions, setFilteredSuggestions] = useState([]);
  const [copySuccess, setCopySuccess] = useState(false);
  const editorRef = useRef(null);

  // Helper function to get color based on variable type
  const getVariableColor = (type) => {
    switch (type) {
      case 'Metric':
        return '#4CAF50'; // green
      case 'Attribute':
        return '#9C27B0'; // purple
      case 'Attribute Value':
        return '#FF9800'; // orange
      default:
        return 'black';
    }
  };

  // Debug logging for selectedPID
  useEffect(() => {
    console.log('Selected PID:', selectedPID);
  }, [selectedPID]);

  const getVariableReference = (variable) => {
    if (!selectedPID) {
      console.warn('No PID selected');
      return '';
    }

    console.log('Creating reference for variable:', variable);
    console.log('Using PID:', selectedPID);

    // Make sure we're using the correct PID identifier
    const pidIdentifier = selectedPID.pid || selectedPID.pidId;

    const baseRef = `/gdc/md/${pidIdentifier}/obj/${variable._id}`;
    const reference = variable.type === 'Attribute Value'
      ? `[${baseRef}/elements?id=${variable.elementId}]`
      : `[${baseRef}]`;

    console.log('Generated reference:', reference);
    return reference;
  };

  useEffect(() => {
    const filtered = variables.filter(v => 
      v.name.toLowerCase().includes(currentWord.toLowerCase())
    );
    setFilteredSuggestions(filtered);
    setSelectedIndex(0);
  }, [currentWord, variables]);

  const handleInputChange = (e) => {
    // Get the current content directly from the contentEditable div
    const content = e.target.innerHTML;
    setCode(content);

    // Get the text content for word matching
    const textContent = e.target.textContent || '';

    // Get cursor position
    const selection = window.getSelection();
    if (selection.rangeCount > 0) {
      const range = selection.getRangeAt(0);
      const preCaretRange = range.cloneRange();
      preCaretRange.selectNodeContents(e.target);
      preCaretRange.setEnd(range.endContainer, range.endOffset);
      const position = preCaretRange.toString().length;
      setCursorPosition(position);

      // Get current word
      const textBeforeCursor = textContent.substring(0, position);
      const wordMatch = textBeforeCursor.match(/\S+$/);
      const currentTypedWord = wordMatch ? wordMatch[0] : '';
      setCurrentWord(currentTypedWord);
      setShowSuggestions(currentTypedWord.length > 0);
    }
  };

  const insertSuggestion = (variable) => {
    const selection = window.getSelection();
    if (!selection.rangeCount) return;

    const range = selection.getRangeAt(0);
    const preCaretRange = range.cloneRange();
    preCaretRange.selectNodeContents(editorRef.current);
    preCaretRange.setEnd(range.endContainer, range.endOffset);

    const textBeforeCursor = preCaretRange.toString();
    const wordMatch = textBeforeCursor.match(/\S+$/);
    const startPos = wordMatch ? textBeforeCursor.length - wordMatch[0].length : textBeforeCursor.length;

    const variableRef = getVariableReference(variable);
    console.log('Inserting variable with reference:', variableRef);

    const styledSpan = document.createElement('span');
    styledSpan.style.color = getVariableColor(variable.type);
    styledSpan.setAttribute('data-reference', variableRef);
    styledSpan.textContent = variable.name;

    // Insert the styled span
    const content = editorRef.current.textContent;
    const beforeText = content.substring(0, startPos);
    const afterText = content.substring(cursorPosition);

    editorRef.current.innerHTML = beforeText;
    editorRef.current.appendChild(styledSpan);
    editorRef.current.innerHTML += afterText;

    // Reset suggestions
    setShowSuggestions(false);

    // Update code state
    setCode(editorRef.current.innerHTML);
  };

  const handleCopyCode = () => {
    if (editorRef.current) {
      console.log('Starting copy operation...');
      console.log('Original HTML:', editorRef.current.innerHTML);

      // Get all spans and their references
      const spans = editorRef.current.getElementsByTagName('span');
      let finalText = editorRef.current.textContent;

      console.log('Initial text content:', finalText);
      console.log('Found spans:', spans.length);

      // Replace each span's visible text with its reference
      Array.from(spans).reverse().forEach(span => {
        const reference = span.getAttribute('data-reference');
        const visibleText = span.textContent;

        console.log('Processing span:', {
          visibleText,
          reference,
          'data-reference': span.getAttribute('data-reference')
        });

        if (reference) {
          const startPos = finalText.indexOf(visibleText);
          if (startPos !== -1) {
            finalText = finalText.substring(0, startPos) + 
                       reference + 
                       finalText.substring(startPos + visibleText.length);
            console.log('Text after replacement:', finalText);
          } else {
            console.warn('Could not find visible text in content:', visibleText);
          }
        } else {
          console.warn('Span missing reference attribute:', span);
        }
      });

      console.log('Final text to copy:', finalText);

      // Copy to clipboard
      navigator.clipboard.writeText(finalText).then(() => {
        console.log('Successfully copied to clipboard');
        setCopySuccess(true);
        setTimeout(() => setCopySuccess(false), 2000);
      }).catch(err => {
        console.error('Failed to copy:', err);
      });
    }
  };

  const handleKeyDown = (e) => {
    if (!showSuggestions) return;

    switch (e.key) {
      case 'ArrowDown':
        e.preventDefault();
        setSelectedIndex(prev => 
          prev < filteredSuggestions.length - 1 ? prev + 1 : prev
        );
        break;
      case 'ArrowUp':
        e.preventDefault();
        setSelectedIndex(prev => prev > 0 ? prev - 1 : prev);
        break;
      case 'Tab':
      case 'Enter':
        e.preventDefault();
        if (filteredSuggestions[selectedIndex]) {
          insertSuggestion(filteredSuggestions[selectedIndex]);
        }
        break;
      case 'Escape':
        setShowSuggestions(false);
        break;
      default:
        break;
    }
  };

  return (
    <div style={{ position: 'relative' }}>
      <div style={{ display: 'flex', justifyContent: 'flex-end', marginBottom: '10px' }}>
        <button 
          onClick={handleCopyCode}
          style={{
            padding: '5px 10px',
            backgroundColor: copySuccess ? '#4CAF50' : '#2196F3',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          {copySuccess ? 'Copied!' : 'Copy Code'}
        </button>
      </div>
      <div 
        ref={editorRef}
        contentEditable={true}
        onInput={handleInputChange}
        onKeyDown={handleKeyDown}
        style={{
          border: '1px solid #ccc',
          minHeight: '200px',
          padding: '10px',
          whiteSpace: 'pre-wrap',
          fontFamily: 'monospace'
        }}
      />

      {showSuggestions && filteredSuggestions.length > 0 && (
        <div style={{
          position: 'absolute',
          top: '100%',
          left: 0,
          backgroundColor: 'white',
          border: '1px solid #ccc',
          maxHeight: '200px',
          overflowY: 'auto',
          width: '200px',
          zIndex: 1000
        }}>
          {filteredSuggestions.map((variable, index) => (
            <div
              key={variable._id}
              onClick={() => insertSuggestion(variable)}
              style={{
                padding: '5px 10px',
                cursor: 'pointer',
                backgroundColor: index === selectedIndex ? '#f0f0f0' : 'white',
                color: getVariableColor(variable.type)
              }}
            >
              {variable.name}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
